package Password;import java.io.Console;public class Demo4 {    public static void main(String[] args) {        Console con =System.console();        if(con!=null)        {            String name=con.readLine("Please provide your name");            char []passdata=con.readPassword("please provide your password");            System.out.println("hell0 "+name+"your login is successful");            String st=String.valueOf(passdata);            System.out.println(st);        }        else {            System.out.println("console is empty");        }    }}