package Password;import java.util.regex.Matcher;import java.util.regex.Pattern;public class Demo5 {    public static void main(String[] args) {        String str="Jon Jonathan JonFrankjon  ken Jon Jon ToddJon";        Pattern p=Pattern.compile("Jon");        Matcher m=p.matcher(str);        System.out.println("(Original) String="+str) ;        str=m.replaceAll("Tinu");        System.out.println("Replace String:-"+str);    }}