package Person;public class Nurse extends Person{    int hrsWorked;    double hrRate;    String shift;    public Nurse(String name,int hw,String shift,double hr){        super(name);        this.hrsWorked=hw;        this.shift=shift;        this.hrRate=hr;    }    public void calculatPayment(){        System.out.println(" "+hrRate*hrsWorked);    }    @Override    public void displayditail(){        System.out.println(name+ " is nurse");        System.out.println("Shift type- "+shift);        System.out.println("Total hors works- "+hrsWorked);        System.out.println("Total hors Rate- "+hrRate);    }}