package Saturday_Lab1.Que_4;import java.util.Scanner;public class Book {    private String bookName;    private int bookPrice;    private String authorName;    static int count; public Book(){     bookName="Shivaji Maharaj";     bookPrice=300;     authorName="nikhil sathe"; }    public Book(String nm, int price, String anm) {        count++;        this.bookName = nm;        this.bookPrice = price;        this.authorName = anm;    }    public void setBookName(String bookName) {        this.bookName = bookName;    }    public String getBookName() {        return bookName;    }    public void setBookPrice(int bookPrice) {        this.bookPrice = bookPrice;    }    public int getBookPrice() {        return bookPrice;    }    public void setAuthorName(String authorName) {        this.authorName = authorName;    }    public String getAuthorName() {        return authorName;    }    public void bookData() {        Scanner sc = new Scanner(System.in);        System.out.println("Enter book Name- ");        bookName = sc.nextLine();        System.out.print("Enter Book Price- ");        bookPrice = sc.nextInt();        sc.nextLine();        System.out.println("Enter Book Author- ");        authorName = sc.nextLine();    }    public void display() {        System.out.println("Enter book Name- " + bookName);        System.out.println("Enter Book Price- " + bookPrice);        System.out.println("Enter Book Author- " + authorName);    }}