package Saturday_Lab1.Que_5;public class Ticket {    private int ticketId;    private int price;    private static int availableTickets;    public static int getAvailableTickets() {        return availableTickets;    }    public int getPrice() {        return price;    }    public int getTicketId() {        return ticketId;    }    public void setTicketId(int ticketId) {        this.ticketId = ticketId;    }    public static void setAvailableTickets(int availableTickets) {        Ticket.availableTickets = availableTickets;    }    public void setPrice(int price) {        this.price = price;    }    public int calculateTotalCost(int noOfTickets) {        if(availableTickets >= noOfTickets) {            availableTickets -= noOfTickets;            return noOfTickets * price;        }        else {            return -1;        }    }}