package Saturday_Lab2.Ticket;public class BusTicket implements Calculate {    private int ticketNo;    private float ticketPrice;    private float totalAmount;    private Person person;    public BusTicket(){        ticketNo=20012;        ticketPrice=150;    }    public int getTicketNo() {        return ticketNo;    }    public void setTicketNo(int ticketNo) {        this.ticketNo = ticketNo;    }    public float getTicketPrice() {        return ticketPrice;    }    public void setTicketPrice(float ticketPrice) {        this.ticketPrice = ticketPrice;    }    public float getTotalAmount() {        return totalAmount;    }    public void setTotalAmount(float totalAmount) {        this.totalAmount = totalAmount;    }    public Person getPerson() {        return person;    }    public void setPerson(Person person) {        this.person = person;    }    @Override    public void calculateTotal() {        float dis=0.0f;        int age=person.getAge();        char gender=person.getGender();        if (age<16) {            dis=0.50f;        } else if (age >= 60) {            dis=0.25f;        } else if (gender=='f') {            dis=0.10f;        }        totalAmount=ticketPrice-(ticketPrice*dis);    }}