package Saturday_Lab2.Vehicle;public class Vehicle implements Loan, Insurance{    private int vehicleNo;    private String modelName;    private String vehicleType;    private double price;    public Vehicle(int vehicleNo, String modelName, String vehicleType, double price) {        this.vehicleNo = vehicleNo;        this.modelName = modelName;        this.vehicleType = vehicleType;        this.price = price;    }    @Override    public double issueLoan() {        switch (vehicleType.toLowerCase()) {            case "4 wheeler":                return 0.75 * price;            case "3 wheeler":                return 0.70 * price;            case "2 wheeler":                return 0.60 * price;            default:                return 0;        }    }    @Override    public double takeInsurance() {        if (price <= 150000) {            return 4000;        } else if (price > 150000 && price <= 300000) {            return 4500;        } else {            return 6000;        }    }    public void show() {        System.out.println("Vehicle No- " + vehicleNo+" Model Name: " + modelName);        System.out.println("Vehicle Type: " + vehicleType+" Vehicle Price: " + price);        System.out.println("Eligible Loan Amount: " + issueLoan());        System.out.println("Insurance Amount: " + takeInsurance());    }}