package Saturday_Lab3;import java.util.InputMismatchException;import java.util.Scanner;public class Camera {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int totalCost;        int no;        try {            System.out.print("Enter Cost Of CCTV Cameras- ");            totalCost = sc.nextInt();            System.out.print("Enter Number of CCTV Cameras Purchased- ");            no = sc.nextInt();            float cost = totalCost/no;            System.out.println("Cost = "+cost);        }catch(InputMismatchException e) {            System.out.println("Input is not an Integer..");        }        catch(ArithmeticException e) {            System.out.println("Cannot Divide by Zero. enter no zero num ...");            e.printStackTrace();        }        finally {            sc.close();        }    }}