package Shorting;public class BubbleSort implements Sortable {        public void sort(int[] ar) {            int total=ar.length;            for (int i=0;i<total-1;i++) {                for (int j=0;j<total-i-1;j++) {                    if (ar[j]>ar[j+1]) {                        int temp = ar[j];                        ar[j] = ar[j+1];                        ar[j+1] = temp;                    }                }            }        }    }