package Streams;import java.util.Arrays;import java.util.Comparator;import java.util.List;import java.util.stream.Collectors;public class Acc_Desc {    public static void main(String[] args) {        List<String> name = Arrays.asList("anuj", "SHREE", "VEDANG", "himanshu","shreyas","chirag","gaurav");        List<String> ascending = name.stream()                .map(n->n.toUpperCase())                .sorted()                .collect(Collectors.toList());        List<String> descending = name.stream()                .map(n->n.toUpperCase())                .sorted(Comparator.reverseOrder())                .collect(Collectors.toList());        System.out.println("Ascending order- "+ascending);        System.out.println("Descending order- "+descending);    }}