package Streams;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import java.util.stream.Collectors;public class Odd_Even {    public static void main(String[] args) {        List<Integer> list = new ArrayList<>(Arrays.asList(1, 2, 4, 8, 9));        List<Integer> evenNo = list.stream()                .filter(val -> val % 2 == 0)                .collect(Collectors.toList());        int evenSum = evenNo.stream().mapToInt(Integer::intValue).sum();        List<Integer> oddNo = list.stream()                .filter(val -> val % 2 != 0)                .collect(Collectors.toList());        int oddSum = oddNo.stream().mapToInt(Integer::intValue).sum();        System.out.println("Even numbers- " + evenNo);        System.out.println("Sum of even numbers-" + evenSum);        System.out.println("Odd numbers- " + oddNo);        System.out.println("Sum of odd numbers- " + oddSum);    }}