package Streams;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import java.util.stream.Collectors;public class UpperToLower {    public static void main(String[] args) {        List<String> name = Arrays.asList("anuj", "SHREE", "VEDANG", "himanshu");        List<String> lowerCase = name.stream()                .map(String::toLowerCase)                .collect(Collectors.toList());        List<String> upperCase = name.stream()                .map(String::toUpperCase)                .collect(Collectors.toList());        System.out.println("Lowercase name- " + lowerCase);        System.out.println("Uppercase names- " + upperCase);    }}