package String;public class Palindrom {    public static void main(String[] args) {        String name="BOOBS";        int total=name.length();        int palin=1;        for (int i=0; i<total;i++) {            if (name.charAt(i)!=name.charAt(total- i-1)) {                palin =0;            }        }        if (palin==1) {            System.out.println(name+" It  palindrome.");        } else {            System.out.println(name+" It not palindrome.");        }    }}