package String;public class String_All_Method {    public static void main(String[] args) {        String s1="hello";        String s2="World";        String s3="world";        System.out.println(s1.concat(s2));        System.out.println(s2.concat(s3));        System.out.println(s3.concat(s1));        System.out.println(s3.replace('r','z'));        System.out.println(s1.contains("hel"));        System.out.println(s1.contains("or"));        System.out.println(s1.charAt(2));        System.out.println(s2.length());        System.out.println(s3.toLowerCase());        System.out.println(s3.toUpperCase());        System.out.println(s1.isEmpty());        String firstName = "luffy ";        String lastName = "uchiha";        System.out.println(firstName.concat(lastName));        String myStr = "Hello, World!";        System.out.println(myStr.substring(7, 12));        System.out.println(s2.compareTo(s3));    }}