package Ticket;public class Person {    private String name;    private char gender;    private int age;    public Person()    {        name="anuj";        gender='m';        age=19;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public char getGender() {        return gender;    }    public void setGender(char gender) {        this.gender = gender;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }}